import gmpy2
from gmpy2 import mpz, isqrt, is_prime, next_prime

def factorize_rsa_modulus(N):
    A = gmpy2.isqrt(N) + 1
    x = gmpy2.isqrt(A**2 - N)
    p = A - x
    q = A + x
    return p, q

# N từ câu hỏi 1
N1 = mpz("")

p1, q1 = factorize_rsa_modulus(N1)
print("Câu hỏi 1:")
print("p:", p1)
print("q:", q1)
print("p * q == N:", p1 * q1 == N1)
